    private void txtcomentarioKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtcomentarioKeyTyped
char c = evt.getKeyChar();
if ((c<'a' || c>'z') && (c<'A' || c>'Z')) evt.consume();
    }//GEN-LAST:event_txtcomentarioKeyTyped

